mapper.select:
SELECT COMM_NO, COMM_WRITER, COMM_CONTENT, COMM_REGDATE
  FROM COMMENTS
 WHERE POST_NO = #{post_no}

mapper.update:
UPDATE COMMENTS
   SET COMM_CONTENT = #{comm_content}
 WHERE POST_NO = #{post_no}
   AND COMM_NO = #{comm_no}

mapper.delete:
DELETE COMMENTS
 WHERE POST_NO = #{post_no}
   AND COMM_NO = #{comm_no}
