mapper.insert:
		INSERT INTO MEMBER(MEM_ID
						 , MEM_PW
						 , MEM_NAME
						 , MEM_TEL
						 , MEM_EMAIL)
    		 VALUES (#{mem_id}
    		 	   , #{mem_pw}
    		 	   , #{mem_name}
    		 	   , #{mem_tel}
    		 	   , #{mem_email})

mapper.update:
UPDATE MEMBER
   SET MEM_PW = #{mem_pw}
	 , MEM_NAME = #{mem_name}
	 , MEM_TEL = #{mem_tel}
	 , MEM_EMAIL = #{mem_email}
 WHERE MEM_ID = #{mem_id}
