mapper.select:
SELECT POST_NO, POST_WRITER, POST_TITLE, POST_CONTENT, POST_VIEW, POST_LIKE, POST_REGDATE
  FROM POST
 WHERE POST_NO = #{post_no}

mapper.update:
UPDATE POST
   SET POST_VIEW = POST_VIEW + 1
 WHERE POST_NO = #{post_no}

UPDATE POST
   SET POST_TITLE = #{post_title}
     , POST_CONTENT = #{post_content}
     , POST_MODDATE = SYSDATE
 WHERE POST_NO = #{post_no}
